import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { FaUser } from "react-icons/fa";
import Spinner from "../components/Spinner";

import { useSelector, useDispatch } from "react-redux";
import { register, reset } from "../features/auth/authSlice";

function Register() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    telefono: "",
    password: "",
    password2: "",
  });

  const { name, email, telefono, password, password2 } = formData;

  const dispatch = useDispatch();
  const navigate = useNavigate();

  const { user, isLoading, isError, isSuccess, message } = useSelector(
    (state) => state.auth
  );

  useEffect(() => {
    if (isError) toast.error(message);

    // Redirect when logged in
    if (isSuccess || user) navigate("/");

    dispatch(reset());
  }, [isError, isSuccess, user, message, navigate, dispatch]);

  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const onSubmit = (e) => {
    e.preventDefault();

    if (password !== password2) {
      return toast.error("passwords do not match");
    }

    const userData = {
      name,
      email,
      telefono,
      password,
    };

    dispatch(register(userData));
  };

  if (isLoading) {
    return <Spinner />;
  }

  return (
    <>
      <section className="heading">
        <h1>
          <FaUser /> REGISTRO
        </h1>

        <p>Por favor complete el formulario para registrarse</p>
      </section>
      <section className="form">
        <form onSubmit={onSubmit}>
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              id="name"
              name="name"
              value={name}
              onChange={onChange}
              placeholder="Nombre y apellido"
              required
            />
          </div>

          <div className="form-group">
            <select
            //falta agregar funcionamiento a la base de datos
              //value={product}
             // onChange={(e) => setProduct(e.target.value)}
            >
              <option value="iPhone">Cual es su rol?</option>
              <option value="iPhone">Usuario</option>
              <option value="Macbook Pro">Desarrollador</option>
              <option value="iMac">Admin</option>
              <option value="iPad">Tester</option>
            </select>
          </div>

          <div className="form-group">
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={email}
              onChange={onChange}
              placeholder="Ingrese su correo electronico"
              required
            />
          </div>

          <div className="form-group">

            <input
            //falta integrar al back
              type="phone"
              className="form-control"
              id="telefono"
              name="telefono"
              value={telefono}
              onChange={onChange}
              placeholder="Ingrese su telefono"
              required
            />
          </div>

          <div className="form-group">
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              value={password}
              onChange={onChange}
              placeholder="Ingrese una contraseña"
              required
            />
          </div>

          <div className="form-group">
            <input
              type="password"
              className="form-control"
              id="password2"
              name="password2"
              value={password2}
              onChange={onChange}
              placeholder="Confirme la contraseña"
              required
            />
          </div>

          <div className="form-group">
            <button type="submit" className="btn btn-block">
              Enviar!!!!
            </button>
          </div>
        </form>
      </section>
    </>
  );
}

export default Register;
